 */
export default class Dialog {

	/**
	 * ダイアログを表示する
	 * 
	 * 利用例
	 * - Dialog.popup("test", 0, "test", Dialog.MB_YESNOCANCEL | Dialog.MB_DEFBUTTON3);
	 * @param {string} text 
	 * @param {number} [secondstowait=0]
	 * @param {string} [caption=""]
	 * @param {number} [type=0]
	 * @returns {number}
	 */
	static popup(text) {
		let secondstowait = 0;
		let caption = "";
		let type = 0;
		let istype = false;
		for(let j = 1; j < arguments.length; j++) {
			if(typeof arguments[j] === "string") {
				caption = arguments[j];
			}
			else {
				if(istype) {
					secondstowait = type;
					type = arguments[j];
				}
				else {
					type = arguments[j];
					istype = true;
				}
			}
		}
		return WScript.CreateObject("WScript.Shell").Popup( text, secondstowait, caption, type );
	}

}

